mapper.select:
select start_date, Stu.id as stuId, Stu.name as stuName, major, grade, Bc.name as courseName, T.name as teacherName, test_mode, score, result from t_score as Sc, t_course as C, t_student as Stu, t_base_course as Bc, t_teacher as T where Sc.c_id = C.id and Sc.s_id = Stu.id and C.base_course_id = Bc.id and C.t_id = T.id and C.complete = 1
and C.start_date >= #{param2.startDate}
and C.start_date <= #{param2.endDate}
and (score >= 60 or result = '通过')
and (60 > score or result = '未通过')
and ( Stu.major like CONCAT('%', #{param2.searchKey}, '%') or Stu.grade like CONCAT('%', #{param2.searchKey}, '%')
or Bc.name like CONCAT('%', #{param2.searchKey}, '%') )
limit #{param1.start}, #{param1.pageSize}

select count(*) from t_score, t_course as C where t_score.c_id = C.id and C.complete = 1 and s_id = #{param1,jdbcType=VARCHAR}
and (score >= 60 or result = '通过')
and (60 > score or result = '未通过')

select start_date, BC.name as courseName, T.name as teacherName, test_mode, score, result from t_score as S, t_course as C, t_teacher as T, t_base_course as BC where S.c_id = C.id and C.base_course_id = BC.id and C.t_id = T.id and C.complete = 1 and S.s_id = #{param2,jdbcType=VARCHAR}
and (score >= 60 or result = '通过')
and (60 > score or result = '未通过')
limit #{param1.start}, #{param1.pageSize}

